    private ArrayList<Funcionario> funcionarios = new ArrayList<Funcionario>(); /* variavel global */
    
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // BOTÃO CRIAR
        
        // Para esta condicional foi tentado realizar txtCRACHA == null.
        // Entretanto, deve-se verificar o conteúdo dos campos de entrada, não os próprios objetos.
        
            if(txtCRACHA.getText().isEmpty()){
                JOptionPane.showMessageDialog(this, "Por favor, preencha o Crachá do funcionário para realizar o seu cadastro.");
            }
            else if(txtNOME.getText().isEmpty()){
                JOptionPane.showMessageDialog(this, "Por favor, preencha o Nome do funcionário para realizar o seu cadastro.");
            }
            else if(txtSALARIO.getText().isEmpty()){
                JOptionPane.showMessageDialog(this, "Por favor, preencha o Salário do funcionário para realizar o seu cadastro.");
            }
            else if(txtDESCONTO.getText().isEmpty()){
                JOptionPane.showMessageDialog(this, "Por favor, preencha o Desconto do funcionário para realizar o seu cadastro.");
            }
            else if(txtVALORHORA.getText().isEmpty()){
                JOptionPane.showMessageDialog(this, "Por favor, preencha o Valor Hora do funcionário para realizar o seu cadastro."); 
            }
            else if(txtQTHORA.getText().isEmpty()){
                JOptionPane.showMessageDialog(this, "Por favor, preencha a Quantidade de Horas do funcionário para realizar o seu cadastro.");  
            }

            else{
        Funcionario funcionario = new Funcionario();  /* variavel local */
        funcionario.setCracha(Integer.parseInt(txtCRACHA.getText()));
        funcionario.setNome(txtNOME.getText());
        funcionario.setTipoVinculo(comboVINCULO.getSelectedItem().toString().charAt(0));
        funcionario.setValorHora(Integer.parseInt(txtVALORHORA.getText()));
        funcionario.setQtdeHora(Integer.parseInt(txtQTHORA.getText()));
        funcionario.setSalario(Integer.parseInt(txtSALARIO.getText()));
        funcionario.setValorDesconto(Integer.parseInt(txtDESCONTO.getText()));
        funcionarios.add(funcionario);
        JOptionPane.showMessageDialog(this, "Funcionário cadastrado e adicionado à lista com sucesso." +
                                                        "\nClique no botão Mostrar para visualizar o seu último cadastro.");  
            }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // BOTÃO MOSTRAR

        if(funcionarios.size() == 0){
            JOptionPane.showMessageDialog(this, "Você não cadastrou nenhum funcionário.");
            }
            else{
            JOptionPane.showMessageDialog(this, 
                                                        funcionarios.get(funcionarios.size()-1).imprimir());
            }
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // BOTÃO ALTERAR

        if (funcionarios.size() == 0){
            JOptionPane.showMessageDialog(this, "No momento você não criou nenhum funcionáriodo para realizar qualquer alteração.");
        }
        else{
        Funcionario funcionario = funcionarios.get(funcionarios.size()-1); /* Variavel local*/
        funcionario.setCracha(Integer.parseInt(txtCRACHA.getText()));
        funcionario.setNome(txtNOME.getText());
        funcionario.setTipoVinculo(comboVINCULO.getSelectedItem().toString().charAt(0));
        funcionario.setValorHora(Integer.parseInt(txtVALORHORA.getText()));
        funcionario.setQtdeHora(Integer.parseInt(txtQTHORA.getText()));
        funcionario.setSalario(Integer.parseInt(txtSALARIO.getText()));
        funcionario.setValorDesconto(Integer.parseInt(txtDESCONTO.getText()));
        JOptionPane.showMessageDialog(this, "O Último funcionário cadastrado foi atualizado com sucesso." +
                                                        "\nClique no botão Mostrar para verificar a sua correção.");
        }
    }//GEN-LAST:event_jButton3ActionPerformed

