    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        // BOTÃO LISTA (em desenvolvimento para melhorias...)

        for (int i = 0; i < funcionarios.size(); i++) {
            JOptionPane.showMessageDialog(this, "Cracha: " + funcionarios.get(i).getCracha() +
                                                            "\nNome: " + funcionarios.get(i).getNome() +
                                                            "\nTipo Vinculo: " + funcionarios.get(i).getTipoVinculo() +
                                                            "\nSalário: " + funcionarios.get(i).getSalario() +
                                                            "\nDesconto: " + funcionarios.get(i).getValorDesconto() +
                                                            "\nValor a Receber: " + funcionarios.get(i).CalcularValorSalario() +
                                                            "\n");
            
        }
        
        
        
    }//GEN-LAST:event_jButton5ActionPerformed


    
    
    
    
    
    
    
    
